  #{total,jdbcType=INTEGER}, #{period,jdbcType=INTEGER}, #{state,jdbcType=INTEGER}, 
  #{updateTime,jdbcType=TIMESTAMP}, #{createTime,jdbcType=TIMESTAMP}, #{lastAddTime,jdbcType=TIMESTAMP}, 
  #{startTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, #{multiple,jdbcType=INTEGER}
  )

